import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import './Link.css';

function Link({ to, href, text, isArrow, isSelected, onClick}) {
    const [isHovered, setIsHovered] = useState(false);
    const [isPressed, setIsPressed] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
        setIsPressed(false);
    };

    const handleMouseDown = () => {
        setIsHovered(false);
        setIsPressed(true);
    };

    const handleMouseUp = () => {
        setIsPressed(false);
        setIsHovered(true);
    };

    const handleClick = (e) => {
        if (href) {
            e.preventDefault(); // Prevent default routing behavior
            window.open(href, '_blank'); // Open the link in a new tab
        }
        onClick(text);
    };

    const linkProps = {
        className: `Project ${isHovered ? 'hovered' : ''} ${isPressed ? 'pressed' : ''}`,
        onClick: handleClick,
        onMouseEnter: handleMouseEnter,
        onMouseLeave: handleMouseLeave,
        onMouseDown: handleMouseDown,
        onMouseUp: handleMouseUp,
    };


