import React from 'react';
import './ProjectHeader.css';
import tinkerImg from '../../Assets/Projects/tinker.png';
import measuredImg from '../../Assets/Projects/Measured.png';
import whatnotImg from '../../Assets/Projects/WhatNot.png';
import eightfoldImg from '../../Assets/Projects/Eightfold.png';
import splitImg from '../../Assets/Projects/Split.png';

const projects = [
    {
        title: "Tinker",
        role: "Product Designer",
        src: tinkerImg,
        description: "As real estate becomes increasingly popular, many new landlords are at a loss. The process of completing their tenant's maintenance orders is tedious and time-consuming.",
        phrase: "Tinker is a contractor market place that streamline landlords’ contractor search.",
        type: "App Design",
        date: "Spring 2024",
        dates: "2/24 - 5/24"
    },
    {
        title: "Measured",
        role: "Product Design Intern @",
        src: measuredImg,
        phrase: "Optimize media spend for businesses through data.",
        type: "Website Design",
        date: "Summer 2023 & 2024"
    },{
        title: "WhatNot",
        role: "Product Design Strategist @",
        src: whatnotImg,
        phrase: "Reimagine the TV to couch experience for a live stream marketplace.",
        type: "App Design",
        date: "Fall 2023"
    },{
        title: "Eightfold.AI",
        role: "Product Design Intern",
        src: eightfoldImg,
        phrase: "Transform how organizations hire, manage, retain, and upskill their workers through data and AI.",
        type: "App Design",
        date: "Summer 2021"
    },{
        title: "Split",
        role: "UI/UX Designer and Researcher",
        src: splitImg,
        phrase: "The app that makes dividing expenses easy.",
        type: "App Design",
        date: "Fall 2023"
    }
]

function ProjectHeader(title){
    let project = projects[0];


    if(title ==="Tinker"){
        project = projects[0];
   }else if(title ==="Measured"){
        project = projects[1];
   }else if(title ==="WhatNot"){
        project = projects[2];
   }else if(title==="Eightfold"){
        project = projects[3];
   }else if(title==="Split"){
       project = projects[4];
   }

    return(
        <div className="ProjectContainer">
             <div className = "ImageProjectContainer">
                <img src = {project.src}/>
            </div>
            <div className="ProjectInfo">
                <div className="TitleSection">
                    <h1>{project.title}</h1>
                    <p>{project.description}</p>
                    <p className="bold">{project.phrase}</p>
                </div>
                <div className="ProjectHeaderDetails"> 
                    <div className="InfoColumn">
                        <p className="tiny">Role</p>
                        <p>{project.role}</p>
                    </div>

                    <div className="InfoColumn">
                        <p className="tiny">Date</p>
                        <p>{project.date}: {project.dates}</p>
                    </div>

                    <div className="InfoColumn">
                        <p className="tiny">Role</p>
                        <p>Product Designer</p>
                    </div>
                </div>

            </div>


        </div>
    )
}

export default ProjectHeader;
