import React, { useState } from 'react';
import logo from '../../Assets/Logo.svg';
import './Header.css';
import Link from '../Link/Link.js';

function Header() {
    const [selectedLink, setSelectedLink] = useState('Work'); // Start with 'Work' selected

    const handleLinkClick = (text) => {
        setSelectedLink(text);
    };

    return (
        <div className="Header">
            <img src={logo} className="Logo" alt="logo" />
            <div className="NavLinks">
                <Link to="/" text="Work" isArrow="false" isSelected={selectedLink === 'Work'} onClick={() => handleLinkClick('Work')} />
                <Link to="/about" text="About Me" isArrow="false" isSelected={selectedLink === 'About'} onClick={() => handleLinkClick('About')} />
                <a href="../Pages/Joanna_Poon_Resume.pdf" target="_blank" rel="noopener noreferrer"></a>
            </div>
        </div>
    );
}

export default Header;
