import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import './Project.css';
import tinkerImg from '../../Assets/Projects/tinker.png';

const projects = [
    {
        title: "Tinker",
        role: "Product Designer",
        src: tinkerImg,
        phrase: "Streamline landlords’ contractor search through a contractor marketplace.",
        type: "App Design",
        date: "Spring 2024"
    }
]

const project = projects[0];

function Project({ to, title}) {
    const [isHovered, setIsHovered] = useState(false);
    const [isPressed, setIsPressed] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
        setIsPressed(false);
    };

    const handleMouseDown = () => {
        setIsHovered(false);
        setIsPressed(true);
    };

    const handleMouseUp = () => {
        setIsPressed(false);
        setIsHovered(true);
    };


    if(title ==="Tinker"){
        const project = projects[0];
    }

    const linkProps = {
        className: `Project ${isHovered ? 'hovered' : ''} ${isPressed ? 'pressed' : ''}`,
        onMouseEnter: handleMouseEnter,
        onMouseLeave: handleMouseLeave,
        onMouseDown: handleMouseDown,
        onMouseUp: handleMouseUp,
    };


    return  (
        <RouterLink to={to} {...linkProps}>
            <img src = {project.src}/>
            <div className = "ProjectInformationContainer">
                <div className = "ProjectInformation">
                    <div className ="TitleContainer">
                        <p>{project.role}</p>
                        <h2>{project.title}</h2>
                    </div>
                </div>
            </div>
        </RouterLink>
    );
}

export default Project;
