import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import './Project.css';

const projects = [
    {
        title: "Tinker",
        src: "src/assets/projects/tinker.png",
        phrase: "Streamline landlords’ contractor search through a contractor marketplace.",
        type: "App Design",
        date: "Spring 2024"
    }
]

function Project({ to, text, onClick}) {
    const [isHovered, setIsHovered] = useState(false);
    const [isPressed, setIsPressed] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
        setIsPressed(false);
    };

    const handleMouseDown = () => {
        setIsHovered(false);
        setIsPressed(true);
    };

    const handleMouseUp = () => {
        setIsPressed(false);
        setIsHovered(true);
    };

    const handleClick = (e) => {

        onClick(text);
    };

    const linkProps = {
        className: `Project ${isHovered ? 'hovered' : ''} ${isPressed ? 'pressed' : ''}`,
        onClick: handleClick,
        onMouseEnter: handleMouseEnter,
        onMouseLeave: handleMouseLeave,
        onMouseDown: handleMouseDown,
        onMouseUp: handleMouseUp,
    };


    return  (
        <RouterLink to={to} {...linkProps}>
        </RouterLink>
    );
}

export default Project;
