import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import Arrow from '../../Assets/Arrow.svg';
import './Link.css';

function Link({ to, text, isArrow, isSelected, onClick }) {
    const handleMouseEnter = () => {
        color="var(--Red-200)";
    };

    const handleMouseLeave = () => {
        // Reset hover state handling here
    };

    const handleMouseDown = () => {
        // Add while pressing state handling here
    };

    const handleMouseUp = () => {
        // Reset while pressing state handling here
    };

    const handleClick = () => {
        onClick(text);
    };

    return (
        <RouterLink
            to={to}
            className={`NavLink ${isSelected ? 'selected' : ''}`}
            onClick={handleClick}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
        >
            <div className="LinkContent">
                <h4>{text}</h4>
                {isArrow === 'true' && <img src={Arrow} className="Arrow" alt="Arrow" />}
            </div>
            <div className="Underline"></div>
        </RouterLink>
    );
}

export default Link;
