import React, { useState } from 'react';
import logo from '../../Assets/Logo.svg';
import './Header.css';
import Link from '../Link/Link.js';

function Header() {
    const [selectedLink, setSelectedLink] = useState('Work'); // Start with 'Home' selected

    const handleLinkClick = (text) => {
        setSelectedLink(text);
    };

    return (
        <div className="Header">
            <img src={logo} className="Logo" alt="logo" />
            <div className="NavLinks">
                <Link to="/" text="Work" isArrow="false" isSelected={selectedLink === 'Home'} onClick={() => handleLinkClick('Work')} />
                <Link to="/about" text="About Me" isArrow="false" isSelected={selectedLink === 'About'} onClick={() => handleLinkClick('About')} />
                <Link to="/resume" text="Resume" isArrow="true" isSelected={selectedLink === 'Resume'} onClick={() => handleLinkClick('Resume')} />
            </div>
        </div>
    );
}

export default Header;
